mapper.select:
SELECT * FROM CATEGORY 

level=#{level}

and cateClass=#{cateClass}

sql.where:
	display ='y' and sellState='y'


	and categoryId like CONCAT(#{cateClass}, '__')


	and categoryId=#{cateId}



and


			(
			P_ID = #{searchKey } or
			title Like CONCAT('%', #{searchKey }, '%') 
			)


			title Like CONCAT('%', #{searchKey }, '%') 


			P_ID = #{searchKey }

mapper.select:
SELECT P_ID as pId,itemId,title, description, translator, author,coverLargeUrl, categoryId, categoryName, publisher, sellCount, stock, saleRate, salePrice,realPrice, regDate, sellState, display, DATE_FORMAT(sellStart, '%Y.%m.%d %h:%m') as sellStart, DATE_FORMAT(sellEnd, '%Y.%m.%d %h:%m') as sellEnd FROM BOOK_GOODS
order by ${sort}
LIMIT #{pageIndex}, #{pageSize}
